import { ConnectionProvider, UnifiedWalletProvider, WalletName } from '@jup-ag/wallet-adapter';
import { WalletAdapterNetwork } from '@solana/wallet-adapter-base';
import React, { PropsWithChildren } from 'react';

import { clusterApiUrl } from '@solana/web3.js';
import { FC, ReactNode, useMemo } from 'react';
import { IInit } from 'src/types';
import { AutoConnectProvider } from './AutoConnectProvider';
import { NetworkConfigurationProvider, useNetworkConfiguration } from './NetworkConfigurationProvider';
import { PreferredExplorerProvider } from './preferredExplorer';

// Built in wallets
import { SolflareWalletAdapter } from '@solana/wallet-adapter-solflare';

export const HARDCODED_WALLET_STANDARDS: { id: string; name: WalletName; url: string; icon: string }[] = [
  {
    id: 'Phantom',
    name: 'Phantom' as WalletName,
    url: 'https://phantom.app',
    icon: 'data:image/svg+xml;base64,',
  },
  {
    id: 'Backpack',
    name: 'Backpack' as WalletName,
    url: 'https://backpack.app',
    icon: 'data:image/png;base64,',
  },
  {
    id: 'OKX Wallet',
    name: 'OKX Wallet' as WalletName,
    url: 'https://www.okx.com/web3',
    icon: 'data:image/png;base64,',
  },
  {
    id: 'Glow',
    name: 'Glow' as WalletName,
    url: 'https://glow.app',
    // TODO: Why is this image so HUGE?!
    icon: 'https://glow.app/landing/app-icons/purple.png',
  },
];

const WalletContextProvider: FC<{ endpoint?: string; children: ReactNode }> = ({ endpoint, children }) => {
  const { networkConfiguration } = useNetworkConfiguration();
  const network = networkConfiguration as WalletAdapterNetwork;
  const selectedEndpoint: string = useMemo(() => endpoint ?? clusterApiUrl(network), [network]);

  const enableWalletPassthrough = (() => {
    if (typeof window === 'undefined') return undefined;
    return window.Jupiter.enableWalletPassthrough;
  })();

  const wallets = useMemo(() => {
    if (enableWalletPassthrough) {
      return [];
    }

    // we'll be hardcoding the other WalletStandards for now.
    return [new SolflareWalletAdapter()];
  }, [network]);

  const ShouldWrapWalletProvider = enableWalletPassthrough
    ? React.Fragment
    : ({ children }: { children: ReactNode }) => (
        <UnifiedWalletProvider
          wallets={wallets}
          config={{
            env: 'mainnet-beta',
            autoConnect: true,
            metadata: {
              name: 'Jupiter Terminal',
              url: 'https://terminal.jup.ag',
              description:
                'An open-sourced, lite version of Jupiter that provides end-to-end swap flow by linking it in your HTML. Check out the visual demo for the various integration modes below.          ',
              iconUrls: [],
            },
            hardcodedWallets: HARDCODED_WALLET_STANDARDS,
            walletPrecedence: [
              'Phantom' as WalletName,
              'Backpack' as WalletName,
              'OKX Wallet' as WalletName,
              'Glow' as WalletName,
            ],
            notificationCallback: undefined,
          }}
        >
          {children}
        </UnifiedWalletProvider>
      );

  return (
    <ConnectionProvider endpoint={selectedEndpoint}>
      <ShouldWrapWalletProvider>{children}</ShouldWrapWalletProvider>
    </ConnectionProvider>
  );
};

export const ContextProvider: React.FC<PropsWithChildren<IInit>> = ({ endpoint, defaultExplorer, children }) => {
  return (
    <>
      <NetworkConfigurationProvider>
        <AutoConnectProvider>
          <WalletContextProvider endpoint={endpoint}>
            <PreferredExplorerProvider defaultExplorer={defaultExplorer}>{children}</PreferredExplorerProvider>
          </WalletContextProvider>
        </AutoConnectProvider>
      </NetworkConfigurationProvider>
    </>
  );
};
